'use client';

import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useViewStore, ViewType } from '@/src/store/viewStore';
import { Button } from '@/src/components/Button';
import { CharacterSelection } from '@/src/views/gamified/CharacterSelection';
import { ProfessionalView } from '@/src/views/normal/ProfessionalView';
import { VerticalBlindTransition } from '@/src/components/VerticalBlindTransition';
import profileData from '@/src/data/profile.json';

export default function Home() {
  const { currentView, setView } = useViewStore();
  const [showTransition, setShowTransition] = React.useState(false);

  const handleStartAdventure = () => {
    // Start the transition first
    setShowTransition(true);
    // After a tiny delay to ensure the "solid" curtain is rendered, switch the view
    // Since showing the transition is instant (phase: solid, z-index 99999), 
    // the user won't see the landing page vanish.
    setTimeout(() => {
      setView('gamified');
    }, 50);
  };

  const handleTransitionComplete = () => {
    setShowTransition(false);
  };

  // Helper to render the actual content
  const renderContent = () => {
    if (currentView === 'normal') {
      return <ProfessionalView />;
    }

    if (currentView === 'gamified') {
      return <CharacterSelection />;
    }

    // Default: Landing Page
    return (
      <div style={{ zoom: '70%' }} className="min-h-[145vh] bg-gradient-to-br from-black via-gray-900 to-black flex items-center justify-center p-4">
        <AnimatePresence>
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="max-w-6xl w-full"
          >
            {/* Header */}
            <motion.div
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.2, duration: 0.6 }}
              className="text-center mb-16"
            >
              <h1 className="text-6xl md:text-8xl font-bold mb-4 bg-gradient-to-r from-cyan-400 via-purple-500 to-amber-400 bg-clip-text text-transparent">
                {profileData.name}
              </h1>
              <p className="text-2xl md:text-3xl text-gray-300 mb-2">{profileData.title}</p>
              <p className="text-lg text-gray-400 italic">{profileData.tagline}</p>
            </motion.div>

            {/* View Selection Cards */}
            <div className="grid md:grid-cols-2 gap-8 mb-12">
              {/* Normal View Card */}
              <motion.div
                initial={{ opacity: 0, x: -50 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.4, duration: 0.6 }}
                whileHover={{ scale: 1.05 }}
                className="bg-gradient-to-br from-gray-800 to-gray-900 border-2 border-gray-700 rounded-2xl p-8 cursor-pointer hover:border-white hover:shadow-[0_0_40px_rgba(255,255,255,0.3)] transition-all duration-300"
                onClick={() => setView('normal')}
              >
                <div className="text-center">
                  <div className="text-6xl mb-4">ðŸ’¼</div>
                  <h2 className="text-3xl font-bold mb-4">Professional View</h2>
                  <p className="text-gray-400 mb-6">
                    Traditional portfolio showcasing skills, projects, and experience in a clean, professional format
                  </p>
                  <Button variant="primary">Enter Professional Mode</Button>
                </div>
              </motion.div>

              {/* Gamified View Card */}
              <motion.div
                initial={{ opacity: 0, x: 50 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.6, duration: 0.6 }}
                whileHover={{ scale: 1.05 }}
                className="bg-gradient-to-br from-purple-900 via-gray-900 to-cyan-900 border-2 border-purple-700 rounded-2xl p-8 cursor-pointer hover:border-cyan-400 hover:shadow-[0_0_40px_rgba(6,182,212,0.4)] transition-all duration-300"
                onClick={handleStartAdventure}
              >
                <div className="text-center">
                  <div className="text-6xl mb-4">ðŸŽ®</div>
                  <h2 className="text-3xl font-bold mb-4 bg-gradient-to-r from-cyan-400 to-purple-400 bg-clip-text text-transparent">
                    Gamified Experience
                  </h2>
                  <p className="text-gray-300 mb-6">
                    Embark on an interactive journey through skills and projects. Choose your character and battle through challenges!
                  </p>
                  <Button variant="neon-purple">Start the Adventure</Button>
                </div>
              </motion.div>
            </div>

            {/* Footer */}
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.8, duration: 0.6 }}
              className="text-center text-gray-500 text-sm"
            >
              <p>Choose your preferred experience above</p>
            </motion.div>
          </motion.div>
        </AnimatePresence>
      </div>
    );
  };

  return (
    <>
      {renderContent()}

      <AnimatePresence mode="wait">
        {showTransition && (
          <VerticalBlindTransition
            key="v-blind"
            onComplete={handleTransitionComplete}
            color="#0f172a"
          />
        )}
      </AnimatePresence>
    </>
  );
}

'use server';

import { Resend } from 'resend';
import { z } from 'zod';

const formSchema = z.object({
    name: z.string().min(2, 'Name must be at least 2 characters'),
    email: z.string().email('Invalid email address'),
    message: z.string().min(10, 'Message must be at least 10 characters'),
});

const resend = new Resend(process.env.RESEND_API_KEY);

export async function sendEmail(prevState: unknown, formData: FormData) {
    const validatedFields = formSchema.safeParse({
        name: formData.get('name'),
        email: formData.get('email'),
        message: formData.get('message'),
    });

    if (!validatedFields.success) {
        return {
            errors: validatedFields.error.flatten().fieldErrors,
            message: 'Missing Fields. Failed to send email.',
        };
    }

    const { name, email, message } = validatedFields.data;

    try {
        if (!process.env.RESEND_API_KEY) {
            console.log('Simulating email send (No API Key):', { name, email, message });
            // Simulate delay
            await new Promise(resolve => setTimeout(resolve, 1000));
            return { success: true, message: 'Email sent successfully! (Simulation)' };
        }

        await resend.emails.send({
            from: 'Portfolio Contact <onboarding@resend.dev>',
            to: 'sahuganesh@gmail.com', // Replace with your email
            subject: `New Message from Portfolio: ${name}`,
            text: `Name: ${name}\nEmail: ${email}\nMessage: ${message}`,
        });

        return { success: true, message: 'Email sent successfully!' };
    } catch (error) {
        return {
            message: 'Database Error: Failed to send email.',
        };
    }
}

'use client';

import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

interface VerticalBlindTransitionProps {
    onComplete?: () => void;
    color?: string;
}

export const VerticalBlindTransition: React.FC<VerticalBlindTransitionProps> = ({
    onComplete,
    color = "#0f172a" // slate-900 or similar
}) => {
    const [phase, setPhase] = useState<'solid' | 'shrink' | 'done'>('solid');
    const numBars = 15;

    useEffect(() => {
        // Step 1: Show solid color for 2 seconds
        const timer1 = setTimeout(() => {
            setPhase('shrink');
        }, 2000);

        return () => clearTimeout(timer1);
    }, []);

    const barVariants = {
        solid: {
            scaleX: 1,
            transition: { duration: 0 }
        },
        shrink: (i: number) => ({
            scaleX: 0,
            transition: {
                duration: 1.5,
                ease: "easeInOut" as const,
                delay: i * 0.05
            }
        })
    };

    return (
        <div className="fixed inset-0 z-[99999] overflow-hidden flex">
            {Array.from({ length: numBars }).map((_, i) => (
                <motion.div
                    key={i}
                    custom={i}
                    initial="solid"
                    animate={phase === 'shrink' ? 'shrink' : 'solid'}
                    variants={barVariants}
                    onAnimationComplete={() => {
                        if (phase === 'shrink' && i === numBars - 1) {
                            onComplete?.();
                        }
                    }}
                    style={{
                        flex: 1,
                        backgroundColor: color,
                        originX: 0.5, // Shrink to center of each bar
                        height: '100%',
                    }}
                />
            ))}
        </div>
    );
};

import * as Phaser from 'phaser';
import { BaseEntity } from './BaseEntity';
import monstersData from '@/src/data/monsters.json';

export class Enemy extends BaseEntity {
    public id: string;
    public type: 'minion' | 'boss';

    // Stats
    public hp: number = 100;
    public damage: number = 10;
    private speed: number = 80;
    private monsterData: any;

    // AI State
    private aiState: 'chase' | 'prepare' | 'attack' | 'defend' | 'death' = 'chase';
    private lastAttackTime: number = 0;
    private target?: Player; // Reference to player for tracking

    constructor(scene: Phaser.Scene, x: number, y: number, monsterId: string) {
        // Determine size based on ID
        const isBoss = monsterId === 'monster2'; // simplistic check
        const w = isBoss ? 100 : 80;
        const h = isBoss ? 180 : 120;

        super(scene, x, y, `${monsterId}-idle`, w, h);

        this.id = monsterId;
        this.type = isBoss ? 'boss' : 'minion';
        this.setDepth(isBoss ? 10 : 100);

        this.loadMonsterData(monsterId);
    }

    private loadMonsterData(id: string) {
        this.monsterData = monstersData.find(m => m.id === id);
        if (this.monsterData) {
            this.hp = this.monsterData.hp;
            this.damage = this.monsterData.damage;
            this.speed = this.monsterData.speed || 80;

            if (this.monsterData.assets?.idle) {
                const scale = this.monsterData.scale || (this.type === 'boss' ? 1.2 : 0.8);
                this.createDOMElement(this.monsterData.assets.idle, scale);
            }
        }
    }

    public setTarget(player: Player) {
        this.target = player;
    }

    public updateAI(time: number) {
        if (!this.target || !this.active || this.aiState === 'death') return;

        const dist = Phaser.Math.Distance.Between(this.x, this.y, this.target.x, this.target.y);

        if (this.aiState === 'chase') {
            // Movement
            if (dist > 100) {
                if (this.x < this.target.x) {
                    this.setVelocityX(this.speed);
                    this.setFlipX(false);
                } else {
                    this.setVelocityX(-this.speed);
                    this.setFlipX(true);
                }
                this.setAnimation('walk');
            } else if (time > this.lastAttackTime + 2000) {
                // Prepare Attack
                this.setVelocityX(0);
                this.aiState = 'prepare';
                this.setAnimation('prepare');

                const prepareDuration = this.monsterData?.timings?.prepareDuration || 1000;
                this.scene.time.delayedCall(prepareDuration, () => {
                    if (this.active && this.aiState === 'prepare') {
                        this.performAttack();
                    }
                });
            } else {
                // Idle (Cooldown)
                this.setVelocityX(0);
                this.setAnimation('idle');
            }
        }
    }

    private performAttack() {
        this.aiState = 'attack';
        const isSpecial = this.type === 'boss' && Phaser.Math.Between(0, 1) === 1;
        const attackKey = isSpecial ? 'special' : 'attack'; // Need asset mapping

        this.setAnimation(attackKey as any);

        const timings = this.monsterData?.timings;
        const hitDelay = timings?.hitDelay || 500;
        const duration = timings?.attackDuration || 1000;

        // Hit Logic
        this.scene.time.delayedCall(hitDelay, () => {
            if (!this.active || !this.target) return;

            const dist = Phaser.Math.Distance.Between(this.x, this.y, this.target.x, this.target.y);
            const range = isSpecial ? 200 : 150;

            if (dist < range) {
                // Deal Damage
                const dmg = isSpecial ? 20 : this.damage;
                this.target.takeDamage(dmg);
                // Camera shake handled by scene usually, but can do here
                this.scene.cameras.main.shake(100, 0.01);
            }
        });

        // Reset
        this.scene.time.delayedCall(duration, () => {
            if (this.active && this.aiState !== 'death') {
                this.aiState = 'chase';
                this.lastAttackTime = this.scene.time.now;
            }
        });
    }

    public takeDamage(amount: number) {
        if (this.aiState === 'death') return;

        this.hp -= amount;
        this.setTint(this.aiState === 'defend' ? 0x888888 : 0xff0000);
        this.scene.time.delayedCall(150, () => this.clearTint());

        // Knockback
        const dir = this.flipX ? 1 : -1; // Push back opposite to facing? simple logic
        this.setVelocityX(dir * 200);
        this.setVelocityY(-200);

        if (this.hp <= 0) {
            this.die();
        }
    }

    private die() {
        if (this.aiState === 'death') return;
        this.aiState = 'death';
        this.setVelocity(0, 0);
        this.setAnimation('death');

        const deathDuration = this.monsterData?.timings?.deathDuration || 800;

        // Immediate visual feedback + cleanup
        this.emit('death', this); // Notify scene

        this.scene.time.delayedCall(deathDuration, () => {
            this.destroy(); // BaseEntity cleans up DOM
        });
    }

    private setAnimation(key: 'idle' | 'walk' | 'attack' | 'prepare' | 'death') {
        if (!this.monsterData?.assets) return;

        // Simple mapping
        let asset = this.monsterData.assets[key];
        // Fallbacks
        if (!asset && key === 'prepare') asset = this.monsterData.assets.idle;

        if (asset) {
            this.updateGif(asset);
        }
    }
}

